SELECT * FROM dual

mapper.delete:
delete from SYS_TREE
where (
ID IN

	#{x_rowId}

)

mapper.select:
select E.*
order by ${orderBy}
order by E.LOCKED asc, E.PARENTID, E.SORTNO asc, E.TREEID asc

mapper.sql:
from SYS_TREE E


	1 = 1


		and E.ID IN

			#{x_nodeId}




		and E.PARENTID = #{parentId}



		and E.PARENTID IN

			#{x_parentId}




		and E.DISCRIMINATOR = #{discriminator}



		and E.NAME = #{name}



		and E.NAME like #{nameLike}



		and E.NODEDESC like #{descLike}



		and E.SORTNO >= #{sortGreaterThanOrEqual}



		and E.SORTNO > #{sortGreaterThan}



		and E.SORTNO <= #{sortLessThanOrEqual}



		and E.SORTNO < #{sortLessThan}



		and E.CODE = #{code}



		and E.CODE like #{codeLike}



		and E.CODE IN

			#{x_code}




		and E.TREEID like #{treeIdLike}



		and E.URL like #{urlLike}



		and E.LOCKED = #{locked}



		and E.CREATEBY = #{createBy}



		and E.DELETEFLAG = #{deleteFlag}
