mapper.update:
update GOODS_OUT_STOCK${tableSuffix}
set
QUANTITY_ = #{quantity, jdbcType=DOUBLE}
where
ID_ = #{id, jdbcType=BIGINT}


update GOODS_OUT_STOCK${tableSuffix}
set BUSINESSSTATUS_ = #{businessStatus, jdbcType=INTEGER},
	CONFIRMBY_ = #{confirmBy, jdbcType=VARCHAR},
	CONFIRMTIME_ = #{confirmTime, jdbcType=TIMESTAMP}
where ID_ = #{id, jdbcType=BIGINT}

mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.BUSINESSSTATUS_ asc, E.OUTSTOCKTIME_ desc

mapper.sql:
from GOODS_OUT_STOCK${tableSuffix} E


	1 = 1


		and E.ID_ IN

			#{x_id}




		and E.TENANTID_ = #{tenantId}



		and E.TENANTID_ IN

			#{x_tenantId}




		and E.GOODSID_ = #{goodsId}



		and E.GOODSID_ IN

			#{x_goodsId}




		and E.GOODSNAME_ like #{goodsNameLike}



		and E.GOODSNODEID_ = #{goodsNodeId}



		and E.OUTSTOCKTIME_ >=
		#{outStockTimeGreaterThanOrEqual}



		and E.OUTSTOCKTIME_ <= #{outStockTimeLessThanOrEqual}



		and E.SEMESTER_ = #{semester}



		and E.YEAR_ = #{year}



		and E.YEAR_ >= #{yearGreaterThanOrEqual}



		and E.MONTH_ = #{month}



		and E.WEEK_ = #{week}



		and E.DAY_ = #{day}



		and E.FULLDAY_ = #{fullDay}



		and E.FULLDAY_ >= #{fullDayGreaterThanOrEqual}



		and E.FULLDAY_ <= #{fullDayLessThanOrEqual}



		and E.QUANTITY_ >= #{quantityGreaterThanOrEqual}



		and E.QUANTITY_ <= #{quantityLessThanOrEqual}



		and E.UNIT_ = #{unit}



		and E.RECEIVERID_ = #{receiverId}



		and E.RECEIVERID_ IN

			#{x_receiverId}




		and E.RECEIVERNAME_ like #{receiverNameLike}



		and E.REMARK_ like #{remarkLike}



		and E.BUSINESSSTATUS_ = #{businessStatus}



		and E.CONFIRMBY_ = #{confirmBy}



		and E.CONFIRMTIME_ >= #{confirmTimeGreaterThanOrEqual}



		and E.CONFIRMTIME_ <= #{confirmTimeLessThanOrEqual}



		and E.CREATEBY_ = #{createBy}



		and E.CREATETIME_ >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME_ <= #{createTimeLessThanOrEqual}
