SELECT * FROM dual

mapper.delete:
delete from HEALTH_MEDICAL_EXAM_GRADE_CNT
where TENANTID_
= #{tenantId}
and
TYPE_ = #{type}
and YEAR_ = #{year}
and MONTH_ =
#{month}

mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.SORTNO_ asc

mapper.sql:
from HEALTH_MEDICAL_EXAM_GRADE_CNT E


	1 = 1


		and E.CHECKID_ = #{checkId}



		and E.CHECKID_ IN

			#{x_checkId}




		and E.TENANTID_ = #{tenantId}



		and E.TENANTID_ IN

			#{x_tenantId}




		and E.GRADEID_ = #{gradeId}



		and E.GRADEID_ IN

			#{x_gradeId}




		and E.YEAR_ = #{year}



		and E.MONTH_ = #{month}



		and E.TYPE_ = #{type}
