mapper.delete:
delete from HEALTH_MEDICAL_SPOT_CHECK
where (
ID_ IN

	#{x_id}

)

mapper.select:
select distinct E.*



		order by ${orderBy}


		order by E.CHECKDATE_ desc

mapper.sql:
from HEALTH_MEDICAL_SPOT_CHECK E


	1 = 1


		and E.SEX_ = #{sex}



		and E.NATION_ = #{nation}



		and E.YEAR_ = #{year}



		and E.MONTH_ = #{month}



		and E.AGEOFTHEMOON_ = #{ageOfTheMoon}



		and E.AGEOFTHEMOON_ >=
		#{ageOfTheMoonGreaterThanOrEqual}



		and E.AGEOFTHEMOON_ <= #{ageOfTheMoonLessThanOrEqual}



		and E.HEIGHTLEVEL_ <= #{heightLevelLessThanOrEqual}



		and E.HEIGHTLEVEL_ >=
		#{heightLevelGreaterThanOrEqual}



		and E.WEIGHTLEVEL_ <= #{weightLevelLessThanOrEqual}



		and E.WEIGHTLEVEL_ >=
		#{weightLevelGreaterThanOrEqual}



		and E.PROVINCE_ like #{provinceLike}



		and E.CITY_ like #{cityLike}



		and E.AREA_ like #{areaLike}



		and E.ORGANIZATION_ like #{organizationLike}



		and E.TYPE_ = #{type}



		and E.CREATEBY_ = #{actorId}
